package Java_web01;

public class web02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final int SAFE_SPEED = 60; // 안전 속도 
		final int MAX_VALUE = 2147483647; 
		final int MIN_VALUE = -2147483648; 
		final String SERVER_IP_ADDRESS = "13.124.56.189";
		
		
		System.out.println("규정 속도는 " + SAFE_SPEED + "km 입니다.");
		System.out.println("int 정수의 최대 값= " + MAX_VALUE);
		System.out.println("int 정수의 최소 값= " + MIN_VALUE);
		System.out.println("서버의 IP 주소는 " + SERVER_IP_ADDRESS + "입니다.");
		System.out.println("원주율은 " + Math.PI);

	}

}
